import React, {Component} from 'react';
import './App.css';
import LinearProgress from "@material-ui/core/LinearProgress";
import MaterialTable from "material-table";
import {Snackbar} from "@material-ui/core";
import MuiAlert from '@material-ui/lab/Alert';
import {createStyles, ThemeProvider} from "@material-ui/styles";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import AppBar from "@material-ui/core/AppBar";
import Typography from "@material-ui/core/Typography";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";

const ws = new WebSocket("ws://127.0.0.1:8080/ws");

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {items: null, lastUpdate: null, updateSnackVisible: false};
    }

    componentDidMount() {
        ws.onopen = () => {
            console.log("Websocket connected");
        };
        ws.onmessage = (msg) => {
            let items = JSON.parse(msg.data);
            this.setState(state => ({
                ...state,
                items: items,
                lastUpdate: Date.now().toString(),
                updateSnackVisible: true
            }))
        }
    }

    renderLoading() {
        return (
            <LinearProgress/>
        )
    }

    renderLoaded() {
        // Customize some columns
        let columns = Object.keys(this.state.items[0]).filter(k => (k !== 'tableData')).map(k => {
                switch (k) {
                    case 'url':
                        return {title: k, field: k, render: rowData => <a href={rowData.url}>{rowData.url}</a>};
                    default:
                        return {title: k, field: k};
                }
            }
        );
        columns.push({
            'title': 'history',
            'field': 'history',
            render: rowData => <a href={"https://camelcamelcamel.com/product/" + rowData.asin} target={"_blank"}>
                <img src={"https://charts.camelcamelcamel.com/us/" + rowData.asin + "/amazon.png"}/>
            </a>
        });

        return (
            <div>
                <MaterialTable columns={columns}
                               data={this.state.items}
                               title={null}
                />
                <Snackbar open={this.state.updateSnackVisible}
                          onClose={() => this.setState(state => ({...state, updateSnackVisible: false}))}
                          autoHideDuration={2000}>
                    <MuiAlert severity="info" elevation={6}
                              variant="filled">{"Updated " + (new Date()).toTimeString()}</MuiAlert>
                </Snackbar>
            </div>
        );
    }



    render() {
        const useStyles = makeStyles((theme: Theme) =>
            createStyles({
                root: {
                    flexGrow: 1,
                },
                menuButton: {
                    marginRight: theme.spacing(2),
                },
                title: {
                    flexGrow: 1,
                },
            }),
        );

        const classes = useStyles();

        return (
            <ThemeProvider theme={createMuiTheme({palette: {type: 'dark'}})}>
                <AppBar position="static">
                    <Toolbar>
                        <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" className={classes.title}>
                            News
                        </Typography>
                        <Button color="inherit">Login</Button>
                    </Toolbar>
                </AppBar>
                <div>{this.state.items === null ? this.renderLoading() : this.renderLoaded()}</div>
            </ThemeProvider>
        )
    }
}

export default App;
